targets.expr:
sum(
  kube_node_status_allocatable{
    resource="cpu",
    node=~"prom-ring-mimir.*",
    node!~"prom-ring-mimir-controlplane.*"
  }
) by (node)

sum(
  kube_node_status_allocatable{
    resource="memory",
    node=~"prom-ring-mimir.*",
    node!~"prom-ring-mimir-controlplane.*"
  }
) by (node)
